-- ----------------------------
-- View structure for goods_culture
-- ----------------------------
DROP VIEW IF EXISTS `goods_culture`;
CREATE ALGORITHM = UNDEFINED SQL SECURITY DEFINER VIEW `goods_culture` AS select `categories`.`id` AS `id`,`categories`.`name` AS `name`,`categories`.`pid` AS `pid`,`categories`.`status` AS `status`,`categories`.`level` AS `level`,`categories`.`group` AS `group`,`categories`.`createdAt` AS `createdAt`,`categories`.`updatedAt` AS `updatedAt` from `categories` where (`categories`.`group` = 'goods_culture');

-- ----------------------------
-- View structure for goods_literature
-- ----------------------------
DROP VIEW IF EXISTS `goods_literature`;
CREATE ALGORITHM = UNDEFINED SQL SECURITY DEFINER VIEW `goods_literature` AS select `categories`.`id` AS `id`,`categories`.`name` AS `name`,`categories`.`pid` AS `pid`,`categories`.`status` AS `status`,`categories`.`level` AS `level`,`categories`.`group` AS `group`,`categories`.`createdAt` AS `createdAt`,`categories`.`updatedAt` AS `updatedAt` from `categories` where (`categories`.`group` = 'goods_literature');

-- ----------------------------
-- View structure for goods_popularity
-- ----------------------------
DROP VIEW IF EXISTS `goods_popularity`;
CREATE ALGORITHM = UNDEFINED SQL SECURITY DEFINER VIEW `goods_popularity` AS select `categories`.`id` AS `id`,`categories`.`name` AS `name`,`categories`.`pid` AS `pid`,`categories`.`status` AS `status`,`categories`.`level` AS `level`,`categories`.`group` AS `group`,`categories`.`createdAt` AS `createdAt`,`categories`.`updatedAt` AS `updatedAt` from `categories` where (`categories`.`group` = 'goods_Popularity');

